//calculate the GCD and LCM of two given numbers

import java.util.Scanner;
public class GCD_LCM 
{
    static int gcd(int x, int y)
    {
        int r=0, a, b;
        a = (x &gt; y) ? x : y; // a is greater number
        b = (x &lt; y) ? x : y; // b is smaller number
 
        r = b;
        while(a % b != 0)
        {
            r = a % b;
            a = b;
            b = r;
        }
        return r;
    }
 
    static int lcm(int x, int y)
    {
        int a;
        a = (x &gt; y) ? x : y; // a is greater number
        while(true)
        {
            if(a % x == 0 &amp;&amp; a % y == 0)
                return a;
            ++a;
        }
    }
 
    public static void main(String args[])
    {
        Scanner input = new Scanner(System.in);
        System.out.println("Enter the two numbers: ");
        int x = input.nextInt();
        int y = input.nextInt();
 
        System.out.println("The GCD of two numbers is: " + gcd(x, y));
        System.out.println("The LCM of two numbers is: " + lcm(x, y));
        input.close();
    }
}

//Find the Unique Factorization of a Given Number

import java.util.HashSet;
import java.util.Scanner;
import java.util.Set;

 public class Unique_Prime_Factors 
{
    static Set primeFactors(long number) 
    {
        long copy = number, i;
   Set primeFactor = new HashSet&lt;&gt;();
       for (i = 2; i &lt;= copy; i++) 
       {
            if (copy % i == 0) 
           {
               primeFactor.add(i);
                 copy /= i;
               i--;
            }
        }
        return primeFactor;
    }

    public static void main(String args[]) 
     {
         Scanner input = new Scanner(System.in);
         long n;
        System.out.println("Enter the number: ");
         n = input.nextLong();
         System.out.println("The Prime Factors of " + n + " is: "
                             + primeFactors(n));
     }
}

