Create a Gray Code

using System;
public class Gray
{
    public static ulong grayEncode(ulong n)
    {
        return n ^ (n &gt;&gt; 1);
    }
 
    public static ulong grayDecode(ulong n)
    {
        ulong i = 1 &lt;&lt; 8 * 64 - 2; //long is 64-bit
        ulong p, b = p = n &amp; i;
 
        while ((i &gt;&gt;= 1) &gt; 0)
            b |= p = n &amp; i ^ p &gt;&gt; 1;
        return b;
    }
 
    public static void Main(string[] args)
    {
        Console.WriteLine("Number\tGray");
        for (ulong i = 0; i &lt; 10; i++)
        {
            Console.WriteLine(string.Format("{0}\t{1}", i, Convert.ToString((long)grayEncode(i), 2)));
 
        }
        Console.Read();
    }
}

//Horner Algorithm

                         import java.util.Scanner;

                            public class Horner
                            {
                            private int sum;
                              /** constructor **/
                            public Horner(int[] cof, int x)
                            {
                                sum = 0;
                                calcSum(cof, x, cof.length - 1);
                                display();
                            }

                              /** Calculate sum **/
                            private void calcSum(int[] cof, int x, int N)
                            {
                                sum = cof[N] * x;
                                for (int i = N - 1; i &gt;= 1; i--)
                                sum = (sum + cof[i]) * x;
                                sum += cof[0];
                            }

                            public void display()
                            {
                                System.out.println("Evaluated sum = "+ sum);
                            }
