    num = Convert.ToInt32(Console.ReadLine());
    Console.WriteLine("Number: " + num); 
    Console.Write("Number in words: ");    
    nextdigit = 0; 
    numdigits = 0; 
    do 
    { 
      nextdigit = num % 10; 
      n[numdigits] = nextdigit; 
      numdigits++; 
      num = num / 10; 
    } while(num &gt; 0); 
    numdigits--; 
    for( ; numdigits &gt;= 0; numdigits--) 
        Console.Write(digits[n[numdigits]] + " "); 
